import React, {useRef, useState, useEffect} from 'react';
import { Form, Button, Alert} from 'react-bootstrap';
import {useAuth} from '../contexts/AuthContext'
import {Link, useHistory} from 'react-router-dom'
import Users from "./Users"
import firebase from 'firebase/app'
import 'firebase/firestore';



class FriendProfile extends React.Component {
    constructor() {
      super()
      const events = [];
      this.state = {
        events
      }
    }

  async getEvents(uid) {
    let db = firebase.firestore();
    let data = await db.collection('events').where('userId', '==', uid).get()
    this.setState({
      events: (data.docs.map(doc => doc.data()))
    });
  }



  render(){
        const { uid } = this.props.location
        this.getEvents(uid);
        let returnView;
        //console.log(this.state.events)
        if(this.state.events.length == 0){
          returnView =  <p>No events to display</p>
        }
        else{
          returnView = this.state.events.map((event) =>
            {
              return <li>{event.eventName}</li>
            })
        }
      return (
        <>
            <h2><strong>User: </strong> {uid}</h2>
            <div>
              <strong>User's Personal Events</strong>
              <ul>
                {returnView}
              </ul>
            </div>
            <div>
              <strong>Joint Events</strong>
              <ul>
                {returnView}
              </ul>
              <Button>Book Time With This Friend!</Button>
            </div>
        </>
      )
  }
}
//<Button onClick={() => setListType('All')}>All Users</Button>

export default FriendProfile;
