import React, { useState } from 'react'
 import "react-pro-sidebar/dist/css/styles.css";
 import "./Sidebar.css";
 import UCLA_logo from "./UCLA_logo.png"

 import {
     ProSidebar,
     Menu,
     MenuItem,
     SidebarHeader,
     SidebarFooter,
     SidebarContent,
   } from "react-pro-sidebar";

 // can choose whatever we want
 import { FaList, FaRegHeart } from "react-icons/fa";
 import { FiHome, FiLogOut, FiArrowLeftCircle, FiArrowRightCircle } from "react-icons/fi";
 import { RiPencilLine } from "react-icons/ri";
 import { BiCog } from "react-icons/bi";

 const Sidebar = () => {
   //create initial menuCollapse state using useState hook
   const [menuCollapse, setMenuCollapse] = useState(false)

   //create a custom function that will change menucollapse state from false to true and true to false
   const menuIconClick = () => {
     //condition checking to change state from true to false and vice versa
     menuCollapse ? setMenuCollapse(false) : setMenuCollapse(true);
   };

   return (
     <>
       <div id="header">
           {/* collapsed props to change menu size using menucollapse state */}
         <ProSidebar collapsed={menuCollapse}>
           <SidebarHeader>
             <img className="ucla_logo" src={UCLA_logo} />
             <span className="closemenu" onClick={menuIconClick}>
                 {/* changing menu collapse icon on click */}             
               {menuCollapse ? (
                 <FiArrowRightCircle/>
               ) : (
                 <FiArrowLeftCircle/>
               )}
             </span>
           </SidebarHeader>
           <SidebarContent>
             <Menu iconShape="square">
               <MenuItem active={true} icon={<FiHome />}>
                 Home
               </MenuItem>
               <MenuItem icon={<FaList />}>Friend List</MenuItem>
               <MenuItem icon={<FaRegHeart />}>Favorite</MenuItem>
               <MenuItem icon={<RiPencilLine />}>Profile</MenuItem>
               <MenuItem icon={<BiCog />}>Settings</MenuItem>
             </Menu>
           </SidebarContent>
           <SidebarFooter>
             <Menu iconShape="square">
               <MenuItem icon={<FiLogOut />}>Logout</MenuItem>
             </Menu>
           </SidebarFooter>
         </ProSidebar>
       </div>
     </>
   );
 };

 export default Sidebar;
