import React, {useRef, useState} from 'react';
import { Form, Button, Alert} from 'react-bootstrap';
import {useAuth} from '../contexts/AuthContext'
import {Link, useHistory} from 'react-router-dom'
import firebase from 'firebase/app'
import Sidebar from '../feature/Sidebar';
import Header from './Header';
import './AddEvent.css'

import 'firebase/firestore';

class AddEventClass extends React.Component {
    constructor() {
      super()
      const dateRef = useRef();
	  const timeRef = useRef();
	  const nameRef = useRef();
      const descriptionRef = useRef();
	  const commentRef = useRef();
	  const {currentUser} = useAuth()
      const history = useHistory()
      this.state = {
        error: '',
        loading: false
      }
    }

    async handleSubmit(e) {
		e.preventDefault()
		try{
            this.setState({
                error: '',
                loading: true
              });
			const db = firebase.firestore()
            let date = dateRef.current.value
            let time = timeRef.current.value
            let name = nameRef.current.value
            let desc = descriptionRef.current.value
			let comment = commentRef.current.value
            let nameArray = name.split(' ')
			//console.log(nameArray)
            let descArray = desc.split(' ')
            let searchArray = nameArray.concat(descArray)

			const data = {
				dateTime: date.concat(' ').concat(time),
				userId: currentUser.email,
				eventName: name,
                description: desc,
				commentList: comment,
				searchValues: searchArray,
				jointEvent: false,
				guest: '',

			  };
			const res = await db.collection('events').doc().set(data);
			this.history.push("/")
		}
		catch(err){
			console.log(err)
            this.setState({
                error: "Failed to add event"
              });
		}
        this.setState({
            loading: false
          });
	}

    render(){

      return (
        <>
            <div className="addEvent">
                <h2>Create Event</h2>
                {error && <Alert variant="danger">{this.state.error}</Alert>}
                <Form onSubmit={this.handleSubmit}>
                <Form.Group id="date">
                    <Form.Label>Date: </Form.Label>
                    <Form.Control type="date" ref={this.dateRef} required />
                </Form.Group>

                <Form.Group id="time">
                    <Form.Label>Time: </Form.Label>
                    <Form.Control type="time" ref={this.timeRef} required />
                </Form.Group>

                <Form.Group id="name">
                        <Form.Label>Event Name: </Form.Label>
                        <Form.Control ref={this.nameRef} required />
                </Form.Group>

                    <Form.Group id="description">
                        <Form.Label>Event Description: </Form.Label>
                        <Form.Control ref={this.descriptionRef} />
                    </Form.Group>

                    <Form.Group id="comment">
                        <Form.Label>Comments: </Form.Label>
                        <Form.Control ref={this.commentRef} required />
                </Form.Group>

                    <Button disabled={this.state.loading} type="submit">Add Event: </Button>
                </Form>
            </div> 

            <div className="addEvent">
                No longer need to schedule an event? <Link to="/">Cancel </Link>
            </div>
        </>
      )
  }
}

export default AddEventClass;
