import React from 'react'
import styled from 'styled-components';
import CommentList from './CommentList';
import AddComment from './AddComment';

const Heading = styled.h1`
  font-weight: bold;
  margin-top: 0px;
  padding-top: 16px;
  text-align: center;
`;

const DetailSection = styled.table` 
  text-align: left;
  padding: 16px;
`;

const LeftData = styled(InfoSectionCell)`
font-weight: bold;
width: 100%;
`;

const RightData = styled(InfoSectionCell)``;


export const EventDetailWindow = ({ item:event, exitOnClose = () => {} }) => {
  const { date, time, description, eventName, jointEvent, guest, eventId } = event || {};

  return (
    <Form>
      <Heading>Detail about your event on { date }</Heading>
      <DetailSection>
        <tbody>
          <tr>
            <LeftData>Event Name:</LeftData>
            <RightData>{ eventName }</RightData>
          </tr>
          <tr>
            <LeftData>Time:</LeftData>
            <RightData>{ time }</RightData>
          </tr>
          <tr>
            <LeftData>Description:</LeftData>
            <RightData>{ description }</RightData>
          </tr>
          {jointEvent &&
            <tr>
              <LeftData>This event with:</LeftData>
              <RightData>{ guest }</RightData>
            </tr>
          }
        </tbody>
      </DetailSection>
      <CommentList eventId={eventId}/>
      <AddComment eventId={eventId}/>
      {/* close button? - if we have time */}
    </Form>
  )
}

export default EventDetailWindow;
